import { Component } from '@angular/core';

@Component({
  selector: 'app-viewdescription',
  templateUrl: './viewdescription.component.html',
  styleUrls: ['./viewdescription.component.css']
})
export class ViewdescriptionComponent {
  selectedService: Service;
  showInfo = false;

  constructor(private serviceDataService: ServiceDataService) { }

  showServiceInfo() {
    this.selectedService = this.serviceDataService.getSelectedService();
    this.showInfo = true;
  }

  hideServiceInfo() {
    this.showInfo = false;
  }
}
