    <div class="form-group">
      <div class="col-md-3">
        <label><strong>Number of adults</strong> </label>
        <input type="number" class="form-control">
      </div>
    </div><br>

    <div class="form-group">
      <div class="col-md-3">
        <label><strong>Number of Children</strong> </label>
        <input type="number" class="form-control">
      </div>
    </div>
    <br>

    <div class="form-group">
      <div class="col-md-3">
        <label><strong>Number of days:</strong></label>
        <input type="number" class="form-control">
      </div>
    </div>
    <br>

    <div class="col-md-3">
      <label><strong> Number of days for each service:</strong></label>
    </div>
    <br>

    <div class="form-group">
      <div class="col-md-3">
        <label><strong>Full Service:</strong></label>
        <div class="input-group">
          <input type="number" class="form-control">
