          <select name="snacksPreference" class="form-control">
            <option value="">Light Snacks</option>
            <option value="">Snacks</option>
          </select>
          <span class="input-group-text"><i class="fa fa-info-circle" style="font-size:15px;color:red" title="Choose a snake preference to add to the selected service"></i></span>
        </div>
      </div>
    </div>
    <br>

    <div class="form-group">
      <div class="col-md-12 text-right">
        <button style="font-size:20px">Calculate</button>
      </div>
