import { Component } from '@angular/core';

@Component({
  selector: 'app-viewdescription',
  templateUrl: './viewdescription.component.html',
  styleUrls: ['./viewdescription.component.css']
})
export class ViewdescriptionComponent {
  services = [
    { name: 'Service A', description: 'Description of Service A' },
    { name: 'Service B', description: 'Description of Service B' },
    { name: 'Service C', description: 'Description of Service C' }
  ];
  selectedService = {};
  showDescription = false;

  showServiceDescription(service: {}) {
    this.selectedService = service;
    this.showDescription = true;
  }

  hideServiceDescription() {
    this.showDescription = false;
  }
}
